// import QRCode from 'qrcodejs2';

// TIP 获取二维码图片（根据custom文本）
get_QrCode_addr (list, dom_id_field, text_filed) {
  this.$nextTick(() => {
    console.log('refs的值为，', this.$refs);
    list.forEach(item => {
      const addr_ref = this.$refs['addr' + item[dom_id_field]];
      console.log('item为，', item);
      console.log('dom_id_field为，', dom_id_field);
      console.log('item[dom_id_field]为，', item[dom_id_field]);
      console.log('addr_ref的值为，', addr_ref);

      // FIXME 注意，这里必须要判断  非空
      if (addr_ref) {
        // 逐个生成，二维码
        const qrcode = new QRCode(addr_ref, {
          text: item[text_filed],
          width: 124,
          height: 124,
          colorDark: '#000000', // 显示颜色
          colorLight: '#ffffff', // 背景颜色
          correctLevel: QRCode.CorrectLevel.H, // 质量高度
        });
      }
    });
  });
},
